'use strict';

// This gulpfile makes use of new JavaScript features.
// Babel handles this without us having to do anything. It just works.
// You can read more about the new JavaScript features here:
// https://babeljs.io/docs/learn-es2015/


import del from 'del';
import babel from 'gulp-babel';
import fs from 'fs';
import imagemin from 'gulp-imagemin';
import { argv } from 'process';
import browserSync from 'browser-sync';

const $ = require('gulp-load-plugins')();
const { src, dest, parallel, series, watch } = require('gulp');
const sass = require('gulp-sass')(require('sass'));
const pkg = JSON.parse(fs.readFileSync('./package.json'));
const reload = browserSync.reload;
const isProd = argv[2] === '--prod';
const terser = require('gulp-terser');
const zipFileName = `bugloos__${pkg.name}__v${pkg.version}`;
const rename = require('gulp-rename');

// Lint JavaScript
function lint() {
  return src(['src/scripts/**/*.js', '!node_modules/**'])
    .pipe($.eslint())
    .pipe($.eslint.format())
    .pipe($.if(!browserSync.active, $.eslint.failAfterError()));
}
exports.lint = lint;

// Optimize images
function images() {
  return src('src/images/**/*')
    .pipe(imagemin([
      imagemin.gifsicle({ interlaced: true }),
      imagemin.mozjpeg({ progressive: true }),
      imagemin.optipng({ optimizationLevel: 5 }),
      imagemin.svgo({
        plugins: [
          { removeViewBox: true },
          { cleanupIDs: false }
        ]
      })
    ]))
    .pipe(dest('assets/images'))
    .pipe($.size({ title: 'images' }));
}
exports.images = images;

// Copy all files at the root level (src)
function copy() {
  return src(
      [
        'src/*',
        '!src/*.html',
        '!src/images-placeholder'
      ],
      {
        dot: true
      }
    )
    .pipe(dest('assets'))
    .pipe($.size({ title: 'copy' }));
}
exports.copy = copy;

// Copy all files at the root level (src)
function fonts(){
  return src('src/fonts/**/*')
    .pipe(dest('assets/fonts/'))
    .pipe($.size({ title: 'copy fonts' }))
}
exports.fonts = fonts;

// Copy all files at the root level (src)
function placeholder(){
  return src('src/images-placeholder/**/*')
    .pipe(dest('assets/images/'))
    .pipe($.size({ title: 'copy fonts' }));
}

exports.placeholder = placeholder;

// Compile and automatically prefix stylesheets
function styles() {
  const AUTOPREFIXER_BROWSERS = [
    'ie >= 10',
    'ie_mob >= 10',
    'ff >= 30',
    'chrome >= 34',
    'safari >= 7',
    'opera >= 23',
    'ios >= 7',
    'android >= 4.4',
    'bb >= 10'
  ];

  // For best performance, don't add Sass partials to `gulp.src`
  return src(['src/styles/**/*.scss', 'src/styles/**/*.css'])
    .pipe($.newer('.tmp/styles'))
    .pipe(
      sass({
        outputStyle: 'expanded',
        precision: 10,
        includePaths: ['.']
      }).on('error', sass.logError)
    )
    .pipe($.autoprefixer(AUTOPREFIXER_BROWSERS))
    // .pipe($.cssnano('main.css'))
    .pipe($.size({ title: 'styles' }))
    // .pipe(rename({
    //   extname: '.min.css'
    // }))
    .pipe(dest('assets/styles'))
    .pipe(dest('.tmp/styles'));
}

exports.styles = styles;


// Concatenate and minify JavaScript. Optionally transpiles ES2015 code to ES5.
// to enable ES2015 support remove the line `'only': 'gulpfile.babel.js',` in the
// `.babelrc` file.
function scripts() {
  return src([
    // Note: Since we are not using useref in the scripts build pipeline,
    //       you need to explicitly list your scripts here in the right order
    //       to be correctly concatenated
    './src/scripts/main.js',
    './src/scripts/date-time-picker.js',
    './src/scripts/header.js',
    './src/scripts/btn.js'
    // Other scripts
  ])
    .pipe($.newer('.tmp/scripts'))
    .pipe($.sourcemaps.init())
    .pipe(babel({
      presets: ['@babel/env']
    }))
    .pipe($.sourcemaps.write())
    .pipe(dest('.tmp/scripts'))
    .pipe($.concat('main.min.js'))
    .pipe(terser())
    // Output files
    .pipe($.size({ title: 'scripts' }))
    .pipe(dest('assets/scripts'))
    .pipe(dest('.tmp/scripts'));
}

exports.scripts = scripts;

// Scan your HTML for assets & optimize them
function html() {
  return ( src('src/**/*.html')
      .pipe(
        $.useref({
          searchPath: ['.', '.tmp', 'src']
        })
      )

      .pipe($.if('*.css', $.cssnano({ safe: true, autoprefixer: false })))
      .pipe($.if('*.js', terser()))

      // Minify any HTML
      .pipe(
        $.if(
          file => $.match(file, '*.html') && isProd,
          $.htmlmin({
            removeComments: true,
            collapseWhitespace: true,
            collapseBooleanAttributes: true,
            removeAttributeQuotes: true,
            removeRedundantAttributes: true,
            removeEmptyAttributes: true,
            removeScriptTypeAttributes: true,
            removeStyleLinkTypeAttributes: true,
            removeOptionalTags: true
          })
        )
      )
      // Output files
      .pipe($.if('*.html', $.size({ title: 'html', showFiles: true })))
      .pipe(dest('assets'))
  );
}

exports.html = html;

// Clean output directory
function clean(cb) {
  del(['.tmp', 'assets/*', '!assets/.git'], { dot: true });
  cb();
}

// Clean tmp Directory
function cleanTempDirectory(cb) {
  // del(['.tmp'], { dot: true });
  // del(['assets/*.html'], { dot: true });
  cb()
}

exports.clean = clean;

// Watch files for changes & reload

// const serve = series(parallel(styles, scripts), function () {
//   browserSync.init({
//     notify: false,
//     // Customize the Browsersync console logging prefix
//     logPrefix: 'WSK',
//     // Allow scroll syncing across breakpoints
//     scrollElementMapping: ['main', '.mdl-layout'],
//     // Run as an https by uncommenting 'https: true'
//     // Note: this uses an unsigned certificate which on first access
//     //       will present a certificate warning in the browser.
//     // https: true,

//     server: {
//       baseDir: ['.tmp', 'src'],
//       routes: {
//         '/node_modules': 'node_modules'
//       }
//     },
//     port: 3000
//   });

//   watch('src/**/*.html').on('change', reload);
//   watch('src/styles/**/*.{scss,css}', styles).on('change', reload);
//   watch('src/scripts/**/*.js', parallel(lint, scripts)).on('change', reload);
//   watch('src/images/**/*').on('change', reload);
// });

function start() {
  browserSync.init({
    notify: false,
    // Customize the Browsersync console logging prefix
    logPrefix: 'WSK',
    // Allow scroll syncing across breakpoints
    scrollElementMapping: ['main', '.mdl-layout'],
    // Run as an https by uncommenting 'https: true'
    // Note: this uses an unsigned certificate which on first access
    //       will present a certificate warning in the browser.
    // https: true,

    server: {
      baseDir: ['.tmp', 'src'],
      routes: {
        '/node_modules': 'node_modules'
      }
    },
    port: 3000
  });

  watch('src/**/*.html').on('change', reload);
  watch('src/styles/**/*.{scss,css}', styles).on('change', reload);
  watch('src/scripts/**/*.js', parallel(lint, scripts)).on('change', reload);
  watch('src/images/**/*').on('change', reload);
}


exports.start = start;

exports.serve = series(parallel(styles, scripts), start);

// Build and serve the output from the dist build
function servedist() {
  browserSync.init({
    notify: false,
    logPrefix: 'WSK',
    // Allow scroll syncing across breakpoints
    scrollElementMapping: ['main'],
    // Run as an https by uncommenting 'https: true'
    // Note: this uses an unsigned certificate which on first access
    //       will present a certificate warning in the browser.
    // https: true,
    server: 'assets',
    port: 3001
  })
}

exports.servedist = servedist;


// Zip demo files
// function zip() {
//   return src('assets/**/*', { dot: true })
//     .pipe($.zip(`${zipFileName}${isProd ? '__PROD' : ''}.zip`))
//     .pipe(dest('assets'));
// }

// exports.zip = zip;


// Zip Release files
// function ziprelease() {
//   return src('.tmp/release/**/*', { dot: true })
//     .pipe($.zip(`${zipFileName}.zip`))
//     .pipe(dest('.tmp'));
// }

// exports.ziprelease = ziprelease;


// Build production files, the default task
exports.default = series(clean, styles, lint, html, scripts, copy, images, fonts);

exports.build = series(clean, styles, html, scripts, copy, images, fonts, cleanTempDirectory);

/**
 * Generrate release commands
 * @return {Array} commands
 */
function getReleaseCommands() {
  const basePath = './.tmp/release';

  const cp = [
    'dist',
    'src',
    '.babelrc',
    '.gitattributes',
    '.gitignore',
    'gulpfile.babel.prod.js',
    'package.prod.json',
    'README.md',
    'CHANGELOG.md',
    'LICENSE'
  ].map(p => `cp -rf ./${p} ${basePath}/${p.replace('.prod', '')}`);

  return [
    `mkdir ${basePath}`,
    ...cp,
    `rm -rf ${basePath}/src/images`,
    `mv ${basePath}/src/images-placeholder ${basePath}/src/images`
  ];
}

exports.getReleaseCommands = getReleaseCommands;

